import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // To handle navigation
import { Routes } from 'react-router-dom';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('host');
  const navigate = useNavigate(); // Initialize the navigate function

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Make the API request to login using fetch
      const response = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password, role }),
      });

      if (response.ok) {
        const data = await response.json();
        console.log(data)
        const { userId, role } = data;

        // Store user ID and role in localStorage
        localStorage.setItem('userId', userId);
        localStorage.setItem('role', role);
if(userId){
    if (role === 'host') {
        navigate('/admin'); // Navigate to the Admin dashboard for hosts
      } else if (role === 'receptionist') {
        navigate('/receptionist');
      }
}
else{
    alert("Tumse na paega")
}
        // Redirect based on the role

      } else {
        alert('Invalid login credentials');
      }
    } catch (error) {
      console.error('Error during login:', error);
      alert('Login failed, please try again.');
    }
  };

  return (
    <div className="bg-gray-100 h-screen flex justify-center items-center">
      <div className="container mx-auto">
        <h1 className="text-2xl font-bold mb-5 text-center">Login</h1>
        <form id="loginForm" className="mt-5" onSubmit={handleSubmit}>
          <div>
            <label htmlFor="username" className="block">
              Username:
            </label>
            <input
              type="text"
              id="username"
              className="border p-2 rounded w-full"
              required
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="password" className="block mt-2">
              Password:
            </label>
            <input
              type="password"
              id="password"
              className="border p-2 rounded w-full"
              required
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div className="mt-4">
            <label htmlFor="role" className="block">
              Login as:
            </label>
            <select
              id="role"
              className="border p-2 rounded w-full"
              required
              value={role}
              onChange={(e) => setRole(e.target.value)}
            >
              <option value="host">Host</option>
              <option value="receptionist">Receptionist</option>
            </select>
          </div>
          <button
            type="submit"
            className="bg-blue-500 text-white p-2 rounded mt-4 w-full"
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
