import React, { useEffect, useState } from 'react';

const Table = () => {
  // State variables
  const [visitorModalVisible, setVisitorModalVisible] = useState(false);
  const [emailModalVisible, setEmailModalVisible] = useState(false);
  const [visits, setVisits] = useState([]);
  const [visitorData, setVisitorData] = useState({
    name: '',
    contact: '',
    visitDate: '',
    reason: '',
  });
  const [emailData, setEmailData] = useState({
    email: '',
    emailPassword: '',
  });

  // Fetch data on component mount
  useEffect(() => {
    const userId = localStorage.getItem('userId');
    if (!localStorage.getItem('role')) {
      window.location.href = 'https://iotcom.io/';
    }

    // Load visits for the logged-in host
    loadVisits(userId);

    // Check if the logged-in host has an email
    checkForEmail(userId);
  }, []);

  // Function to load visits
  const loadVisits = async (userId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/visitors?hostId=${userId}`);
      const visitsData = await response.json();
      setVisits(visitsData);
    } catch (error) {
      console.error('Error fetching visits:', error);
    }
  };

  // Function to check if the user has an email
  const checkForEmail = async (userId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/users/${userId}`);
      const user = await response.json();
      if (!user.email || !user.emailPassword) {
        setEmailModalVisible(true);
      }
    } catch (error) {
      console.error('Error fetching user:', error);
    }
  };

  // Handle visitor submission
  const handleVisitorSubmit = async (e) => {
    e.preventDefault();
    const hostId = localStorage.getItem('userId');
    try {
      const response = await fetch('http://localhost:5000/api/visitorss', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...visitorData, hostId }),
      });
      if (response.ok) {
        alert('Visitor successfully registered');
        setVisitorModalVisible(false);
        setVisitorData({ name: '', contact: '', visitDate: '', reason: '' });
        loadVisits(hostId); // Refresh the visit list
      } else {
        alert('Error registering visitor');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // Handle email submission
  const handleEmailSubmit = async (e) => {
    e.preventDefault();
    const userId = localStorage.getItem('userId');
    try {
      const response = await fetch(`http://localhost:5000/api/users/${userId}/updateEmail`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(emailData),
      });
      if (response.ok) {
        alert('Email updated successfully');
        setEmailModalVisible(false);
      }
    } catch (error) {
      console.error('Error updating email:', error);
    }
  };

  return (
    <div className="bg-gray-100 min-h-screen p-10">
      <div className="mb-6">
        <h2 className="text-2xl font-bold mb-4">Visitor Logs</h2>
        <div className="flex justify-between mb-4">
          {/* Search Bar */}
          <input
            type="text"
            className="border border-gray-300 p-2 rounded w-1/2"
            placeholder="Search by visitor name..."
          />
          {/* Date Filters */}
          <div className="flex space-x-4">
            <input type="date" className="border border-gray-300 p-2 rounded" />
            <input type="date" className="border border-gray-300 p-2 rounded" />
            <button className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700 transition">
              Filter
            </button>
          </div>
        </div>
        <button
          onClick={() => setVisitorModalVisible(true)}
          className="bg-[#35A2F4] text-white px-4 py-2 rounded hover:bg-[#2e7db9] transition"
        >
          Add Visitor
        </button>
      </div>

      {/* Visitor Table */}
      <table className="min-w-full bg-white">
        <thead className="bg-gray-100">
          <tr>
            <th className="py-2 px-4 border-b">Name</th>
            <th className="py-2 px-4 border-b">Contact</th>
            <th className="py-2 px-4 border-b">Visit Date</th>
            <th className="py-2 px-4 border-b">Reason</th>
          </tr>
        </thead>
        <tbody className="text-center">
          {visits.map((visit, index) => (
            <tr key={index}>
              <td>{visit.name}</td>
              <td>{visit.contact}</td>
              <td>{new Date(visit.visitDate).toLocaleString()}</td>
              <td>{visit.reason}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Visitor Modal */}
      {visitorModalVisible && (
        <div className="modal fixed inset-0 flex items-center justify-center bg-gray-700 bg-opacity-75">
          <div className="modal-content bg-white p-8 rounded shadow-lg">
            <h2 className="text-xl font-bold mb-4">Register a Visitor</h2>
            <form onSubmit={handleVisitorSubmit} className="space-y-4">
              <input
                type="text"
                value={visitorData.name}
                onChange={(e) => setVisitorData({ ...visitorData, name: e.target.value })}
                placeholder="Visitor Name"
                className="border border-gray-300 p-2 rounded w-full"
              />
              <input
                type="text"
                value={visitorData.contact}
                onChange={(e) => setVisitorData({ ...visitorData, contact: e.target.value })}
                placeholder="Contact"
                className="border border-gray-300 p-2 rounded w-full"
              />
              <input
                type="date"
                value={visitorData.visitDate}
                onChange={(e) => setVisitorData({ ...visitorData, visitDate: e.target.value })}
                className="border border-gray-300 p-2 rounded w-full"
              />
              <input
                type="text"
                value={visitorData.reason}
                onChange={(e) => setVisitorData({ ...visitorData, reason: e.target.value })}
                placeholder="Reason for Visit"
                className="border border-gray-300 p-2 rounded w-full"
              />
              <button
                type="submit"
                className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
              >
                Submit
              </button>
              <button
                type="button"
                onClick={() => setVisitorModalVisible(false)}
                className="bg-gray-300 text-black px-4 py-2 rounded hover:bg-gray-400 transition"
              >
                Close
              </button>
            </form>
          </div>
        </div>
      )}

      {/* Email Modal */}
      {emailModalVisible && (
        <div className="modal fixed inset-0 flex items-center justify-center bg-gray-700 bg-opacity-75">
          <div className="modal-content bg-white p-8 rounded shadow-lg">
            <h2 className="text-xl font-bold mb-4">Update Email</h2>
            <form onSubmit={handleEmailSubmit} className="space-y-4">
              <input
                type="email"
                value={emailData.email}
                onChange={(e) => setEmailData({ ...emailData, email: e.target.value })}
                placeholder="Your Email"
                className="border border-gray-300 p-2 rounded w-full"
              />
              <input
                type="password"
                value={emailData.emailPassword}
                onChange={(e) => setEmailData({ ...emailData, emailPassword: e.target.value })}
                placeholder="Email Password"
                className="border border-gray-300 p-2 rounded w-full"
              />
              <button
                type="submit"
                className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
              >
                Submit
              </button>
              <button
                type="button"
                onClick={() => setEmailModalVisible(false)}
                className="bg-gray-300 text-black px-4 py-2 rounded hover:bg-gray-400 transition"
              >
                Close
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Table;
